import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './pages/Login/Login';
import Register from './pages/Register/Register';
import Student from './pages/Student/Student';
import Instructor from './pages/Instructor/Instructor';
import MyCourses from './pages/MyCourses/MyCourses';
import CourseDetail from './components/CourseDetail/CourseDetail';
import './App.css';

const App = () => {
    const userRole = localStorage.getItem('userRole');
    const token = localStorage.getItem('token');

    return (
        <Router>
            <div className="app">
                <Routes>
                    <Route path="/login" element={!token ? <Login /> : <Navigate to={`/${userRole}`} replace />} />
                    <Route path="/register" element={!token ? <Register /> : <Navigate to={`/${userRole}`} replace />} />
                    <Route path="/student" element={token && userRole === 'student' ? <Student /> : <Navigate to="/login" replace />} />
                    <Route path="/instructor" element={token && userRole === 'instructor' ? <Instructor /> : <Navigate to="/login" replace />} />
                    <Route path="/my-courses" element={token && userRole === 'student' ? <MyCourses /> : <Navigate to="/login" replace />} />
                    <Route path="/course/:id" element={token ? <CourseDetail /> : <Navigate to="/login" replace />} />
                    <Route path="/" element={<Navigate to="/login" replace />} />
                </Routes>
            </div>
        </Router>
    );
};

export default App;
