import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import CourseCard from '../../components/CourseCard/CourseCard';
import './MyCourses.css';

const API_URL = process.env.NODE_ENV === 'production' ? '' : 'http://localhost:5000';

const MyCourses = () => {
    const navigate = useNavigate();
    const [allCourses, setAllCourses] = useState([]);
    const [enrolledCourses, setEnrolledCourses] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (!token) {
            navigate('/login');
            return;
        }
        fetchAllCourses();
        fetchEnrolledCourses();
    }, [navigate]);

    const fetchAllCourses = async () => {
        try {
            setLoading(true);
            const response = await fetch(`${API_URL}/api/courses`);
            if (!response.ok) {
                throw new Error('Failed to fetch available courses');
            }
            const data = await response.json();
            setAllCourses(data);
            setError(null);
        } catch (error) {
            console.error('Error fetching courses:', error);
            setError(error.message);
        } finally {
            setLoading(false);
        }
    };

    const fetchEnrolledCourses = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`${API_URL}/api/courses/enrolled`, {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });
            if (!response.ok) {
                throw new Error('Failed to fetch enrolled courses');
            }
            const data = await response.json();
            setEnrolledCourses(data);
        } catch (error) {
            console.error('Error fetching enrolled courses:', error);
        }
    };

    const handleEnroll = async (courseId) => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`${API_URL}/api/courses/${courseId}/enroll`, {
                method: 'POST',
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });
            
            const data = await response.json();
            
            if (!response.ok) {
                throw new Error(data.message || 'Failed to enroll in course');
            }

            await fetchEnrolledCourses();
            alert('Successfully enrolled in course!');
        } catch (error) {
            console.error('Error enrolling in course:', error);
            alert(error.message);
        }
    };

    // Filter out enrolled courses from available courses
    const availableCourses = allCourses.filter(course => 
        !enrolledCourses.some(enrolled => enrolled._id === course._id)
    );

    return (
        <div className="my-courses-container">
            <h1>My Courses</h1>

            {loading ? (
                <div className="loading">Loading courses...</div>
            ) : error ? (
                <div className="error">{error}</div>
            ) : (
                <>
                    <div className="courses-section">
                        <h2>My Enrolled Courses</h2>
                        {enrolledCourses.length === 0 ? (
                            <p className="no-courses">You haven't enrolled in any courses yet.</p>
                        ) : (
                            <div className="courses-grid">
                                {enrolledCourses.map(course => (
                                    <CourseCard 
                                        key={course._id} 
                                        course={course}
                                        onClick={() => window.location.href = `/course/${course._id}`}
                                    />
                                ))}
                            </div>
                        )}
                    </div>

                    <div className="courses-section">
                        <h2>Available Courses</h2>
                        {availableCourses.length === 0 ? (
                            <p className="no-courses">No courses available at the moment.</p>
                        ) : (
                            <div className="courses-grid">
                                {availableCourses.map(course => (
                                    <CourseCard 
                                        key={course._id} 
                                        course={course} 
                                        onEnroll={handleEnroll}
                                    />
                                ))}
                            </div>
                        )}
                    </div>
                </>
            )}
        </div>
    );
};

export default MyCourses;
