import React, { useState, useEffect } from 'react';
import CourseCard from '../../components/CourseCard/CourseCard';
import './Instructor.css';

const Instructor = () => {
    const [courses, setCourses] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [showCreateCourse, setShowCreateCourse] = useState(false);
    const [newCourse, setNewCourse] = useState({
        title: '',
        description: '',
        category: '',
        level: 'Beginner',
        price: 0,
        thumbnail: ''
    });

    useEffect(() => {
        fetchInstructorCourses();
    }, []);

    const fetchInstructorCourses = async () => {
        try {
            setLoading(true);
            const token = localStorage.getItem('token');
            const response = await fetch('http://localhost:5000/api/courses/instructor', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });
            
            if (!response.ok) {
                throw new Error('Failed to fetch courses');
            }
            
            const data = await response.json();
            setCourses(Array.isArray(data) ? data : []);
            setError(null);
        } catch (error) {
            console.error('Error fetching courses:', error);
            setError('Failed to load courses. Please try again later.');
            setCourses([]);
        } finally {
            setLoading(false);
        }
    };

    const handleCreateCourse = async (e) => {
        e.preventDefault();
        try {
            const token = localStorage.getItem('token');
            const response = await fetch('http://localhost:5000/api/courses', {
                method: 'POST',
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(newCourse)
            });

            if (!response.ok) {
                throw new Error('Failed to create course');
            }

            const data = await response.json();
            setCourses(prevCourses => [...prevCourses, data]);
            setShowCreateCourse(false);
            setNewCourse({
                title: '',
                description: '',
                category: '',
                level: 'Beginner',
                price: 0,
                thumbnail: ''
            });
        } catch (error) {
            console.error('Error creating course:', error);
            alert('Failed to create course. Please try again.');
        }
    };

    const handleAddLesson = async (courseId, lesson) => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:5000/api/courses/${courseId}/lessons`, {
                method: 'POST',
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(lesson)
            });

            if (!response.ok) {
                throw new Error('Failed to add lesson');
            }

            fetchInstructorCourses();
        } catch (error) {
            console.error('Error adding lesson:', error);
            alert('Failed to add lesson. Please try again.');
        }
    };

    if (loading) {
        return (
            <div className="instructor-container">
                <h1>Instructor Dashboard</h1>
                <div className="loading">Loading courses...</div>
            </div>
        );
    }

    if (error) {
        return (
            <div className="instructor-container">
                <h1>Instructor Dashboard</h1>
                <div className="error">{error}</div>
            </div>
        );
    }

    return (
        <div className="instructor-container">
            <h1>Instructor Dashboard</h1>
            
            <div className="instructor-actions">
                <button 
                    className="create-course-btn"
                    onClick={() => setShowCreateCourse(!showCreateCourse)}
                >
                    {showCreateCourse ? 'Cancel' : 'Create New Course'}
                </button>
            </div>

            {showCreateCourse && (
                <div className="create-course-form">
                    <h2>Create New Course</h2>
                    <form onSubmit={handleCreateCourse}>
                        <div className="form-group">
                            <label>Title</label>
                            <input
                                type="text"
                                value={newCourse.title}
                                onChange={(e) => setNewCourse({...newCourse, title: e.target.value})}
                                required
                            />
                        </div>
                        <div className="form-group">
                            <label>Description</label>
                            <textarea
                                value={newCourse.description}
                                onChange={(e) => setNewCourse({...newCourse, description: e.target.value})}
                                required
                            />
                        </div>
                        <div className="form-group">
                            <label>Category</label>
                            <input
                                type="text"
                                value={newCourse.category}
                                onChange={(e) => setNewCourse({...newCourse, category: e.target.value})}
                                required
                            />
                        </div>
                        <div className="form-group">
                            <label>Level</label>
                            <select
                                value={newCourse.level}
                                onChange={(e) => setNewCourse({...newCourse, level: e.target.value})}
                                required
                            >
                                <option value="Beginner">Beginner</option>
                                <option value="Intermediate">Intermediate</option>
                                <option value="Advanced">Advanced</option>
                            </select>
                        </div>
                        <div className="form-group">
                            <label>Price</label>
                            <input
                                type="number"
                                value={newCourse.price}
                                onChange={(e) => setNewCourse({...newCourse, price: e.target.value})}
                                required
                            />
                        </div>
                        <div className="form-group">
                            <label>Thumbnail URL</label>
                            <input
                                type="text"
                                value={newCourse.thumbnail}
                                onChange={(e) => setNewCourse({...newCourse, thumbnail: e.target.value})}
                                required
                            />
                        </div>
                        <button type="submit" className="submit-btn">Create Course</button>
                    </form>
                </div>
            )}

            <div className="courses-grid">
                {courses.length > 0 ? (
                    courses.map(course => (
                        <CourseCard key={course._id} course={course} />
                    ))
                ) : (
                    <div className="no-courses">
                        <p>No courses found. Create your first course!</p>
                    </div>
                )}
            </div>
        </div>
    );
};

export default Instructor;
